import styled from 'styled-components';

const appearFromLeft = keyframes`
  from {
    opacity: 0;
    transform: translateX(-50px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
`;

export const Header = styled.div`
  position: absolute;
  background-color: #000000;
  width: 100%;
  height: 75px;

  h2 {
    position: relative;
    color: #fff;
    width: 25%;
    text-align: center;
    margin: 0 auto;
    top: 20px;
  }
`;
