import styled from 'styled-components';
import { shade } from 'polished';

export const Container = styled.button`
  background-color: #ff9000;
  border-radius: 10px;
  border: 0;
  height: 56px;
  padding: 0 16px;
  width: 100%;
  color: #312e38;
  margin-top: 16px;
  font-weight: 500;

  transition: background 0.2s;

  &:hover {
    background: ${shade(0.2, '#ff9000')};
  }

  &:active {
    background: ${shade(0, '#ff9000')};
  }
`;
